import React, {Component, useState} from 'react'

class Modal extends Component{
    modal = (props)=>{
        const {open, close, header} = props;

        return(
            <div className={open ? 'openModal modal':'modal'}>
                {open?(
                    <section>
                        <header>
                            {header}
                            <button className="close"onclick={close}>$times;</button>
                        </header>
                        <main>
                            {props.children}
                        </main>
                        <footer>
                            <button className="close"onclick={close}>close</button>
                        </footer>
                    </section>
                ):null}
            </div>
        )
    }
}
export default class Board extends Component {

    
    modalOpen=useState(false);
    setModalOpen=useState(false);
    openModal =()=>{
        this.setModalOpen(true);
    }
    closeModal =()=>{
        this.setModalOpen(false);
    }
    render() {
        return (
            <div className="RCA-board-container">
                <button className="RCA-board-button" onclick={this.openModal}>편집</button>
                <Modal open={this.modalOpen}close={this.closeModal}header="Modal heading">
                    리액트형 모달
                </Modal>
                <h3>공지사항</h3>
            </div>
        )
    }
}
