import React, { Component } from "react";
import "./style/DrawIn.css";
import axios from "axios";

export default class DrawIn extends Component {
    state = {
        text: ""
    };

    DrawHandler = (e) => {
        const { text, value } = e.target;
        this.setState({ [text]: value });
        console.log(e);
    };

    DrawClickHandler = () => {
        const { text } = this.state;
        axios.post('/api/board',(text));
    };

    render() {
        const { isOpen, close } = this.props;   //아까 버튼에서 props로 가져온것
        return (
            <>
                {isOpen ? (
                    <div className="modal">
                        <div onClick={close}>
                            <div className="DrawModal">
                <span className="close" onClick={close}>
                  &times;
                </span>
                                <div className="modalContents" onClick={isOpen}>
                                    <input
                                        text="text"
                                        className="TextId"
                                        type="text"
                                        placeholder="공지사항을 적어주세요"
                                        onChange={this.DrawHandler}
                                    />
                                    <button className="DrawBtn" onClick={console.log("e")}>
                                        {" "}
                                        작성{" "}
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                ) : null}
            </>
        );
    }
}
