import React, {Component} from 'react'
import modal from "./modal";

export default class Board extends Component {

    state = {
        todos: [
            "Take your mask",
            "Wash your hands",
            "Drink more water"
        ],
        selectedTodo: undefined
    }

    componentDidMount() {
        try {
            const todos = localStorage.getItem("todos")
            if (todos) {
                console.log(todos)
                this.setState(() => ({ todos: JSON.parse(todos) }))
            }
        } catch (e) {
        }
    }

    componentDidUpdate(prevProps, prevState) {
        if (prevState.todos.length !== this.state.todos.length) {
            localStorage.setItem("todos", JSON.stringify(this.state.todos))
        }
    }

    onRemoveAllClicked = () => {
        this.setState(() => {
            return {
                todos: []
            }
        })
    }

    onNextTodoClicked = () => {
        const index = Math.floor(Math.random() * this.state.todos.length)
        // alert(this.state.todos[index])
        const option = this.state.todos[index]
        this.setState(() => (
            {
                selectedTodo: option
            }
        ))
    }

    onModalClose = () => {
        console.log('clicked')
        this.setState(() => (
            {
                selectedTodo: undefined
            }
        ))
    }

    handleAddTodo = (todo) => {
        if (!todo) {
            return 'Please type vaild item'
        } else if (this.state.todos.indexOf(todo) > -1) {
            return 'Duplicated item typed'
        }

        this.setState((prevState) => {
            return {
                todos: prevState.todos.concat(todo)
            }
        })
    }


    render() {
        return (
            <div className="RCA-board-container">
                <modal
                    onModalClose={this.onModalClose}
                    selectedTodo={this.state.selectedTodo} />
                <button className="RCA-board-button" >편집</button>
                <h3>공지사항</h3>
            </div>
        )
    }
}
